import React from 'react';

import './App.css';

function About(props) {

  console.log("props", props)
  var files_list = props.list
  useEffect(() => {
    refresh(); //children function of interest
  }, [props.refresh]);
  
    return (
      
      <div className="About">
       
       <div className="col-md-8">
         
          <table className="" border="1">
            <thead>
          <tr><th>Name</th><th>Type</th><th>Date</th><th>Link</th></tr>
          </thead>
          <tbody>
          {files_list.map((entry, index) => (
              <tr key={index}>
              <td>{entry.fileName}</td><td>{entry.fileType}</td><td>{entry.date}</td><td>{entry.url}</td>
              </tr>
    ))}
    </tbody>
                </table>


          </div> 
      </div>
    );
  }
  
  export default About;
