import React from 'react';
import Title from './Title';
import Aboutus from './Aboutus';
import './App.css';
import { Button } from 'reactstrap';
const mystyle = {
  color: "white",
 
  padding: "10px",
  fontFamily: "Arial"
};

function App() {
  function on_change(e) {
    console.log(e)
  }

  function ValidateSize(file) {
    var myfile = document.getElementById("file").files[0]
    let fileExtension = myfile.name.split(".")
    fileExtension = fileExtension[fileExtension.length-1]

    let accepted_types = ["png", "doc", "docx", "pdf", "jpg"]

    if(accepted_types.indexOf(fileExtension) < 0)
    {
        alert("Allowed types are: png, doc, docx, pdf, jpg")
        document.getElementById("file").files[0] = null
        return false;
    }

    console.log(fileExtension)
    return
}


  function send_file(e) {
    alert("Sending file")
  }


  return (
    <div className="App">
      <Title />
      <header className="App-header">
       {
       
  
        <div className="container">
          <div className="row centerr">
            <div className="col-md-8">
              <form method="post" action="#" id="#">
                <div className="form-group files">
                  <label>Upload Your File </label>
                  <input type="file" id="file" name="myCVFile" onChange={ValidateSize} className="form-control" />
                </div>
              </form>
            </div>
            
            </div>
            <button onClick={send_file} className="button1">Upload</button>
          </div>


           

        
      
    }
 
       
     </header>
      <Aboutus/>
    </div>
  
  );
}

export default App;
